/**
 * Given two strings A and B, find the minimum number of times A has to be repeated such that B is a substring of it. If no such solution, return -1.

    For example, with A = "abcd" and B = "cdabcdab".

    Return 3, because by repeating A three times (“abcdabcdabcd”), B is a substring of it; and B is not a substring of A repeated two times ("abcdabcd").

    Note:
    The length of A and B will be between 1 and 10000.
 */
import java.util.*;

class RepeatedStringMatch {
    public int repeatedStringMatch(String A, String B) {
        if (A.length() == 1 && getUniqeCount(B) > 1) {
            return -1;
        }
        int count = 0;
        StringBuilder sb = new StringBuilder();
        while (sb.length() <= 10000) {
            if (sb.indexOf(B) == -1) {
                count++;
                sb.append(A);
                if (sb.length() > 10000) {
                    count=-1;
                }
            } else {
                break;
            }
        }
        return count;
    }

    private int getUniqeCount( String arg ) {
        ArrayList<Character> unique = new ArrayList<Character>();
        for( int i = 0; i < arg.length(); i++)
            if( !unique.contains( arg.charAt( i ) ) )
                unique.add( arg.charAt( i ) );
        return unique.size();
    }

    public static void main(String[] args) {
        RepeatedStringMatch r = new RepeatedStringMatch();
        System.out.println(r.repeatedStringMatch("a",""));
    }
}